if ( $save == 1)
{
	if ( is_array($_POST['chat_admin_groups']) )
	{
		$_POST['chat_admin_groups'] = implode(",",$_POST['chat_admin_groups']);
	}
	else
	{
		$_POST['chat_admin_groups'] = "";
	}

	$key = 'chat_admin_groups';
}
if ( $show == 1 )
{
    $key='chat_admin_groups[]';
}

if ( $show == 1 )
{

	if ( ! $this->ipsclass->DB->sql_can_fulltext() )
	{
		$full_text = "<span style='color:red'><b>您的服务器不可以使用全文搜索.</b></span>";
		$index = "";
	}
	else
	{
		$full_text = "<b>您的服务器可以使用全文搜索.</b>";
		if ( ! $this->ipsclass->admin->get_fulltextindex_status() )
		{
			$index = "<span style='color:red'><b>全文索引没有建立.</b></span>: <a href='{$this->ipsclass->base_url}&section=tools&act=op&code=dofulltext'>现在建立索引!</a>";
		}
		else
		{
			$index = "<b>全文索引已经建立.</b>";
		}
	}

	$r['conf_description'] = str_replace( "<!--FULLTEXT-->", $full_text, $r['conf_description'] );
	$r['conf_description'] = str_replace( "<!--FULLCHECK-->", $index, $r['conf_description'] );
}

